const Reducer = (state, action) => {
  switch(action.type) {
    case "SIGNIN_START":
      return {
        user: null,
        isFetching: true,
        error: false
      }
    case "SIGNIN_SUCCESS":
      return {
        user: action.payload,
        isFetching: false,
        error: false
      }
    case "SIGNIN_FAILURE":
      return {
        user: null,
        isFetching: false,
        error: true
      }
      case "UPDATE_START":
        return {
          ...state,
          isFetching: true
        }
      case "UPDATE_SUCCESS":
        return {
          user: action.payload,
          isFetching: false,
          error: false
        }
      case "UPDATE_FAILURE":
        return {
          user: state.user,
          isFetching: false,
          error: true
        }
    case "SIGNOUT":
      return {
        user: null,
        isFetching: false,
        error: false
      }
      default:
        return state
  }
}

export default Reducer
